import React from 'react'
import { StyleSheet, Text, View } from 'react-native'
import { FlatList, TouchableOpacity } from 'react-native-gesture-handler'
import AsyncStorage from '@react-native-community/async-storage'
import { decks } from '../utils/_DATA'

class DeckList extends React.Component {
    state = {
        DATA: this.formatData(decks),
    }

    formatData(decks) {
        let entries = Object.entries(decks)
        var formattedData = []

        for (let i = 0; i < entries.length; i++) {
            let deckEntry = {
                title: entries[i][0],
                subtitle: entries[i][1].questions.length + " cards"
            }

            formattedData.push(deckEntry)
        }

        return formattedData
    }

    render() {
        const { DATA } = this.state

        const DeckItem = ({ title, subtitle }) => (
            <TouchableOpacity
                onPress={() => this.props.navigation.navigate('Deck Details', {
                    title: title,
                    subtitle: subtitle
                })}
            >
                <View style={styles.item}>
                        <Text style={styles.title}>{title}</Text>
                        <Text style={styles.subtitle}>{subtitle}</Text>
                </View>
            </TouchableOpacity>
        )

        const renderItem = ({ item }) => (
            <DeckItem title={item.title} subtitle={item.subtitle}/>
        )

        const AddDeckButton = () => {
            return (
                <TouchableOpacity
                    onPress={() => this.props.navigation.navigate('Create Deck')}
                >
                    <Text style={styles.button}>CREATE DECK</Text>
                </TouchableOpacity>
            )
        }

        return (
            <View style={styles.container}>
                <FlatList 
                    data={DATA}
                    renderItem={renderItem}
                    keyExtractor={item => item.title}
                />
                <AddDeckButton />
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
    item: {
        backgroundColor: 'rgb(10, 125, 240)',
        borderRadius: 10,
        marginLeft: 10,
        marginRight: 10,
        marginTop: 10,
        padding: 20,
    },
    title: {
        fontSize: 20,
        color: 'white',
        fontWeight: 'bold'
    },
    subtitle: {
        color: 'white'
    },
    button: {
        fontSize: 15,
        fontWeight: 'bold',
        color: 'rgb(10, 125, 240)',
        height: 80,
        textAlign: 'center',
        padding: 15,
        letterSpacing: 1
    }
  })


export default DeckList
